import{_ as a}from"./plugin-vue_export-helper-c27b6911.js";import{r as t,o as s,c as r,b as e,d as n,e as o,f as c}from"./app-c78d4e1a.js";const l={},d=c('<ul><li>Descriptive Analytics – What has happened?</li><li>Predictive Analytics – What might happen?</li><li><span style="color:orange;">Prescriptive Analytics</span> – What should we do? What is the best course of action for a given situation?</li></ul><h2 id="prescriptive-analytics" tabindex="-1"><a class="header-anchor" href="#prescriptive-analytics" aria-hidden="true">#</a> Prescriptive Analytics</h2><ul><li>Self-driving cars</li><li>Automatically adjusting airline ticket prices based on customer demand, weather and oil prices</li><li>What TV show will be popular?</li><li>Diagnosis and treatment suggestions in health care</li><li>Decision on mortgage, loan, credit card application</li></ul><p>To be able to make a decision, to compare possible actions, we need to quantify the effect of future decisions…</p><ul><li>Students – grades</li><li>Machine Learning – loss function</li><li>Clustering – distance</li><li>Finance – return and risk</li><li>Banking – credit scoring</li></ul><h2 id="identify-prescribe-a-number-of-different-possible-actionsquantify-the-effect-of-future-decisions-in-order-to-advise-on-possible-outcomes-before-the-decisions-are-actually-made" tabindex="-1"><a class="header-anchor" href="#identify-prescribe-a-number-of-different-possible-actionsquantify-the-effect-of-future-decisions-in-order-to-advise-on-possible-outcomes-before-the-decisions-are-actually-made" aria-hidden="true">#</a> <strong>Identify</strong> (prescribe) a number of different possible actions<br><strong>Quantify</strong> the effect of future decisions in order to advise on possible outcomes before the decisions are actually made</h2><p>Use business rules, algorithms, machine learning and computational modelling procedures</p><p>A right to explanation is a legal right to be given an explanation for an output of the algorithm</p><h2 id="credit-scoring-and-credit-scorecards" tabindex="-1"><a class="header-anchor" href="#credit-scoring-and-credit-scorecards" aria-hidden="true">#</a> Credit Scoring and Credit Scorecards</h2><ul><li>Credit scoring is defined as a statistical model that assigns a risk value to prospective or existing credit accounts.</li><li>A credit scorecard is a statistical risk model that was put into a special format designed for ease of interpretation.</li><li>After the credit scorecard model is built, it is used to make strategic decisions such as accepting/rejecting applicants and deciding when to raise a credit line, as well as other decisions.</li></ul><p>[page 8]</p><h2 id="reference" tabindex="-1"><a class="header-anchor" href="#reference" aria-hidden="true">#</a> Reference</h2>',12),h={href:"https://lo.unisa.edu.au/course/view.php?id=31025&sectionid=788067",target:"_blank",rel:"noopener noreferrer"};function u(f,p){const i=t("ExternalLinkIcon");return s(),r("div",null,[d,e("p",null,[e("a",h,[n("Tim's slides of Week 9"),o(i)])])])}const b=a(l,[["render",u],["__file","Week9.html.vue"]]);export{b as default};
